import React, { useState } from 'react';
import classes from './ContactUs.module.css';
import axios from 'axios';
import Spinner from '../../components/Spinner/Spinner';
import Backdrop from '../../components/Navigation/SideDrawer/Backdrop/Backdrop';

const ContactUs = (props) => {

    const [spinnerState,setSpinnerState] = useState({loading: false});


    const [formData,setFormData] = useState({name: "",contact: "",email: "",description: ""});

    const formHandler = () => {
        setSpinnerState({loading: true});
        axios.post('https://compilation-tech-default-rtdb.firebaseio.com/contactus.json'+formData).
            then(response => {
                setSpinnerState({loading: false});
                console.log("<<<<<<<<<<<<<< response >>>>>>>>>");
                console.log(response);
                console.log("<<<<<<<<<<<<<<  >>>>>>>>>");
            }).
            catch(error=>{
                console.log("<<<<<<<<<<<<<< error >>>>>>>>>");
                console.log(response);
                console.log("<<<<<<<<<<<<<<  >>>>>>>>>");
                setSpinnerState({loading: false});
            });
    }

    const inputChangedHandler = (event,identifier) => {

        let updateedFormValues = {
                ...formData,
                [identifier]: event.target.value
        }
        setFormData(updateedFormValues);
    }
    const hideSideDrawerMethod = () => {
        //
    }
    let theSpinner = null;
    let backDrop = null;
    if(spinnerState.loading){
        theSpinner = <Spinner />
        backDrop = <Backdrop sideDrawerState={spinnerState.loading} clicked={hideSideDrawerMethod}/>
    }

    return <div id={props.id}>
               <div className={classes.ContactUs}>
                    <div className={classes.ContactUs__header}>
                        <h1>Contact Us</h1>
                        <div className={classes.ContactUs__underline}></div>
                    </div>

                    <div className={classes.ContactUs_Spinner}>
                            {backDrop}
                            {theSpinner}
                    </div>

                    <div className={classes.ContactUsContent}>
                            <div className={classes.ContactLeft}>
                                    <div>
                                        <h1>Compilation Tech</h1>
                                    </div>
                                    <div>
                                        <p>We believe that working <strong>Together</strong> we can accomplish <strong>Everything</strong>.</p>
                                    </div>
                                    <div className={classes.ContactInfo}>
                                        <strong>Phone: </strong><span>(+27) 6712 34277</span><br/>
                                        <strong>Email:</strong><span>compilationtech@gmail.com</span>
                                    </div>

                            </div>
                            <div className={classes.ContactRight}>

                                <h1>Get In Toch With Us</h1>
                                <form onSubmit={formHandler} className={classes.ContactForm}>
                                    <input type="text" name="name" placeholder="Name" value={formData.name} onChange={event => inputChangedHandler(event,"name")}/>
                                    <input type="tel" name="telephone" placeholder="Contact" value={formData.contact} onChange={event => inputChangedHandler(event,"contact")}/>
                                    <input type="email" name="email" placeholder="Email" value={formData.email} onChange={event => inputChangedHandler(event,"email")}/>
                                    <textarea cols="40" rows="5" name="description" placeholder="Description" value={formData.description} onChange={event => inputChangedHandler(event,"description")}></textarea>
                                    <button>SUBMIT</button>
                                </form>

                            </div>

                        </div>
            </div>
            <footer className={classes.ContactFooter}>
                    <p>Copyrights © 2021 All Rights Reserved By Compilation Tech</p>
            </footer>
        </div>
}

export default ContactUs;
