import classes from './ImageSlider.module.css';
import React, { useEffect,useState,useRef,useCallback } from 'react';
const ImageSlider = (props) => {

    const images = props.imageProperties;

    let [counter,setCounter] = useState({"a":0,"b":0});
    const temp = useRef(null);

    const selectImageSlide = (id) => {
        temp.current = -1;
        setCounter({"a":id,"b":-1});
    }

    const incrementCounter = useCallback(() =>{
        if(counter.a === 7){
            setCounter({"a":0,"b":-1});
        }else{
            setCounter({"a":counter.a+1,"b":counter.b});
        };
    },[counter.a,counter.b]);

    useEffect(() => {
        temp.current = props.slideId.b;
    }, [props.slideId]);

    useEffect(() => {
        if(counter.b === -1 && temp.current === -1){
            const timer = setTimeout(() => {
                incrementCounter();
            }, 10000);
            return () => clearTimeout(timer);
        }

    }, [counter,props.slideId.a,props.slideId.b,incrementCounter]);

    console.log("Hi There");

    const radioButtons = props.radioButtons.map(radio =>{
            return <div className={classes.RadioInput}>
                            <input type="radio" name="radio-btn" onClick={selectImageSlide(radio.id)}/>  
                   </div>
    });

    return <div>
               <div className={classes.ImageSlider}>
                   {images[counter.a]}
               </div>
               <div className={classes.RadioBtnSlider}>
                 {radioButtons}
                </div>
           </div>

}

export default ImageSlider;
