import classes from './ImageSlider.module.css';
import RadioBtnSlider from './RadioBtnSlider/RadioBtnSlider';
import React, { useEffect,useState,useCallback } from 'react';
const ImageSlider = (props) => {

    const images = props.imageProperties;

    const radioProperties = [{"id":0,"category":"web","checked": false},
                            {"id":1,"category":"web","checked": false},
                            {"id":2,"category":"web","checked": false},
                            {"id":3,"category":"mobile","checked": false},
                            {"id":4,"category":"mobile","checked": false},
                            {"id":5,"category":"mobile","checked": false},
                            {"id":6,"category":"automation","checked": false},
                            {"id":7,"category":"automation","checked": false}
                           ];

    let updateRatioProperties = (id) => {
        return radioProperties = radioProperties.map(radioProperty =>{
            return radioProperty.id === id ? {...radioProperty,checked: true} : radioProperty;
        });
    }

    let [counter,setCounter] = useState(0);



    const controlSlider = (id) => {
        updateRatioProperties(id);
        setCounter(id);
    }

    const incrementCounter = useCallback(() =>{
        if(counter === 7){
            updateRatioProperties(counter);
            setCounter(0);
        }else{
            updateRatioProperties(counter+1);
            setCounter(counter+1);
        };
    },[counter]);

    useEffect(() => {
            const timer = setTimeout(() => {
                incrementCounter();
            }, 3000);
            return () => clearTimeout(timer);

    }, [incrementCounter]);

    console.log("Hi There");

    console.log("<<<<<<>>>>>>>")
    console.log(radioProperties);
    console.log("<<<<<<>>>>>>>")



    const radioButtons = <RadioBtnSlider radioProperties={radioProperties} selectImage={controlSlider}/> 

    return <div>
               <div className={classes.ImageSlider}>
                   {images[counter]}
               </div>
               <div className={classes.RadioBtnSlider}>
                 {radioButtons}
                </div>
           </div>

}

export default ImageSlider;
