import Toolbar from '../../components/Navigation/Toolbar/Toolbar';
import SideDrawer from '../../components/Navigation/SideDrawer/SideDrawer';

import actions from '../../store/action/index';
import {useDispatch, useSelector } from 'react-redux';


const layout = (props) => {

    const dispatch = useDispatch();
    const showSideDrawer = useSelector(state => {
        return !state.layout.showSideDrawer;
    });

    const showSideDrawerMethod =showSideDrawer => dispatch(actions.sideDrawerAction(showSideDrawer)); 

    return <div>
           <Toolbar showSideDrawer={showSideDrawerMethod}/>
           <SideDrawer/>
           <main>{props.children}</main>
    </div>

}

export default layout;
