import ImageSlider from '../../components/ImageSlider/ImageSlider';
import RadioBtnSlider from '../../components/RadioBtnSlider/RadioBtnSlider';
import classes from './WhatWeDo.module.css';
import React, { useState } from 'react';

const Whatwedo = () => {

    let radioProperties = [{"id":0,"category":"web","checked": false},
                             {"id":1,"category":"web","checked": false},
                             {"id":2,"category":"web","checked": false},
                             {"id":3,"category":"mobile","checked": false},
                             {"id":4,"category":"mobile","checked": false},
                             {"id":5,"category":"mobile","checked": false},
                             {"id":6,"category":"automation","checked": false},
                             {"id":7,"category":"automation","checked": false}
                            ];

    let [slideIdState,setSlideIdState] = useState({"a":0,"b":-1});
    let [radioButtonProperties,setRadioButtonProperties] = useState(radioProperties);

    const imageProperties = [{"image_details": "slide first","image":"/images/web/web1.jpg","category":"web"},
                             {"image_details": "slide second","image":"/images/web/web2.jpg","category":"web"},
                             {"image_details": "slide third","image":"/images/web/web3.jpg","category":"web"},
                             {"image_details": "slide forth","image":"/images/mobile/mobile1.jpg","category":"mobile"},
                             {"image_details": "slide fith","image":"/images/mobile/mobile2.jpg","category":"mobile"},
                             {"image_details": "slide sixth","image":"/images/mobile/mobile3.jpg","category":"mobile"},
                             {"image_details": "slide seventh","image":"/images/automation/automation1.png","category":"automation"},
                             {"image_details": "slide eighth","image":"/images/automation/automation2.jpg","category":"automation"}
                            ];

    const controlSlider = (id) => {
        setSlideIdState({"a":id,"b":0});
    }   

    const updateRadioButton = (id) => {
        console.log("id: "+id);
        let radioButtons = radioButtonProperties.map(element => {
            return element.id ===id ? {...element,checked: true} : element;
        }); 

        setRadioButtonProperties(radioButtons);


    }

    console.log("What we do");

    return <div className={classes.WhatWeDo}>
                <ImageSlider imageProperties={imageProperties} slideId={slideIdState} radioButton={updateRadioButton}/>
                <RadioBtnSlider radioProperties={radioButtonProperties} selectImage={controlSlider}/> 
           </div>

}

export default Whatwedo;
