import NavigationItems from '../NavigationItems/NavigationItems';
import Logo from '../../Logo/Logo';
import classes from './SideDrawer.module.css';
import { Redirect } from 'react-router-dom';
import Backdrop from './Backdrop/Backdrop';

import { useDispatch, useSelector } from 'react-redux';
import * as actions from '../../../store/actions/index';
import React, { useState,useEffect } from 'react';



const Sidedrawer = () => {

   const [moveToHome, setMoveToHome] = useState(false);

   const dispatch = useDispatch();
   const sideDrawerState = useSelector(state=> {
      return state.layout.sideDrawerState;
   });

   const hideSideDrawerMethod = () => dispatch(actions.sideDrawerAction(false));


   const navigateToHomePage = () => {
      setMoveToHome(true);
   }


   // useEffect(()=>{
   //    setMoveToHome(false); 
   // },[moveToHome]);

   console.log("renderSideDrawer");

   let sideDrawer = <div className={classes.SideDrawer}>
                        <div>
                           <Logo clicked={navigateToHomePage}/>
                        </div>
                        <nav>
                           <NavigationItems clicked={hideSideDrawerMethod}/> 
                        </nav>
                     </div>;
   if(moveToHome){
      return null;
   }
   else if(sideDrawerState || moveToHome) {
       return <div>
                   {moveToHome ? <Redirect to="/" /> : null}
                   {sideDrawer}
                   <Backdrop sideDrawerState={sideDrawerState} clicked={hideSideDrawerMethod}/>;
              </div>
   }else{
      return null;
   }
}

export default Sidedrawer;
