import classes from './ImageSlider.module.css';
import React, { useEffect,useState } from 'react';
const ImageSlider = (props) => {

    let [counter,setCounter] = useState(props.slideId);

    const images = props.imageProperties.map(imageDetail => {
        return <div className={imageDetail.image_details}>
                    <img src={imageDetail.image} alt={imageDetail.category} key={imageDetail.image} />
               </div>
    });

    setTimeout(() => {
        console.log("setTimeout: "+counter);
        if(counter == 7){
            counter = 0;
            console.log(counter);
            setCounter(counter);
         }else{
            counter = counter +1;
            console.log(counter);
            setCounter(counter);
         }
    } , 10000);


    useEffect(()=>{
        console.log("useEffect: "+props.slideId);
        // const timerId = timer();
        // clearTimeout(timerId);
        // timer();
        setCounter(props.slideId);
    },[props.slideId]);




    return <div className={classes.ImageSlider}>
                {images[counter]}
           </div>

}

export default ImageSlider;
