import NavigationItems from '../NavigationItems/NavigationItems';
import Logo from '../../Logo/Logo';
import classes from './SideDrawer.module.css';
import React, { useState,useEffect } from 'react';
import { Redirect } from 'react-router-dom';



const Sidedrawer = (props) => {

   const [moveToHome, setMoveToHome] = useState(false);
   const [sideDrawerCondition, setSideDrawer] = useState(false);

   const navigateToHomePage = () => {
      setMoveToHome(true);
   }
   const hideSideDrawerMethod = () => {
      setSideDrawer(!sideDrawerCondition);
   }

   useEffect(()=>{
      setMoveToHome(false); 
   },[moveToHome]);

   console.log("renderSideDrawer");

   let sideDrawer = <div className={classes.SideDrawer}>
                        {moveToHome ? <Redirect to="/" /> : null}
                        <Backdrop sideDrawerState={sideDrawerCondition} clicked={hideSideDrawerMethod}/>
                        <div>
                           <Logo clicked={navigateToHomePage}/>
                        </div>
                        <nav>
                           <NavigationItems closeSideDrawer={props.clicked}/> 
                        </nav>
                     </div>;

   if(props.sideDrawerState) {
       return sideDrawer;
   }else{
      return null;
   }
}

export default Sidedrawer;
