import Toolbar from '../../components/Navigation/Toolbar/Toolbar';
import SideDrawer from '../../components/Navigation/SideDrawer/SideDrawer';
import Backdrop from '../../components/Navigation/SideDrawer/Backdrop/Backdrop';

import React, { useState } from 'react';
import classes from './Layout.module.css';
import { Redirect } from 'react-router-dom';

const Layout = (props) => {

    const [sideDrawer, setSideDrawer] = useState(false);
    const [moveToHome, setMoveToHome] = useState(false);

    const showSideDrawerMethod = () => {
        setSideDrawer(!sideDrawer);
    }
    const hideSideDrawerMethod = () => {
        setSideDrawer(!sideDrawer);
    }
    const navigateToHomePage = () => {
        setMoveToHome(true);
    }

    useEffect(()=>{
        setMoveToHome(false); 
    },[moveToHome]);

    console.log("renderLayout")

    return <div className={classes.Layout}>
                {moveToHome ? <Redirect to="/" /> : null}
                <Backdrop sideDrawerState={sideDrawer} clicked={hideSideDrawerMethod}/>
                <Toolbar showSideDrawer={showSideDrawerMethod} sideDrawerState={sideDrawer} clicked={hideSideDrawerMethod} moveToHome={navigateToHomePage}/>
                <SideDrawer sideDrawerState={sideDrawer} clicked={hideSideDrawerMethod} moveToHome={navigateToHomePage}/>
           <main>{props.children}</main>
    </div>

}


export default Layout;
