import * as actionTypes from './actionTypes';

export const sideDrawerAction = (sideDrawerState) => {

    return {
        type: actionTypes.SIDE_DRAWER_CONTROL,
        sideDrawerState:  sideDrawerState
    };

};

export const showDrawerToggle = (showDrawerState) => {

    return {
        type: actionTypes.SHOW_DRAWER_TOGGLE,
        showDrawerState:  showDrawerState
    };

};

export const moveToHomePage = (moveToHomePageSate) => {

    return {
        type: actionTypes.SHOW_DRAWER_TOGGLE,
        moveToHomePageSate:  moveToHomePageSate
    };

};
