import classes from './ImageSlider.module.css';
import React, { useEffect,useState } from 'react';
const ImageSlider = (props) => {

    let [slideCounter,setCounter] = useState({"counter": 0});

    const images = props.imageProperties.map(imageDetail => {
        return <div className={imageDetail.image_details}>
                    <img src={imageDetail.image} alt={imageDetail.category} key={imageDetail.image} />
               </div>
    });

    const timer = setTimeout(() => {
        console.log("setTimeout: "+slideCounter.counter);
        if(slideCounter.counter == 7){
            console.log(slideCounter.counter);
            setCounter({"counter": 0});
         }else{
            console.log(slideCounter.counter);
            setCounter({"counter": slideCounter.counter + 1});
         }
    } , 10000);


    useEffect(()=>{
        console.log({"useEffect": props.slideId});
        clearTimeout(timer);
        setCounter({"counter": props.slideId});
    },[props.slideId]);




    return <div className={classes.ImageSlider}>
                {images[slideCounter.counter]}
           </div>

}

export default ImageSlider;
