import classes from './ImageSlider.module.css';
import React, { useEffect,useState,useRef,useCallback } from 'react';
const ImageSlider = (props) => {

    let [counter,setCounter] = useState({"a":0,"b":0});
    const temp = useRef(null);



    const incrementCounter = useCallback(() =>{
        if(counter.a === 7){
            setCounter({"a":0,"b":-1});
        }else{
            setCounter({"a":counter.a+1,"b":counter.b});
        };
    },[counter.a,counter.b]);

    useEffect(() => {
        temp.current = props.slideId.b;
    }, [props.slideId]);

    useEffect(() => {
        if(counter.b === -1 && temp.current === -1){
            const timer = setTimeout(() => {
                incrementCounter();
            }, 10000);
            return () => clearTimeout(timer);
        }else{
            temp.current = -1;
            setCounter({"a":props.slideId.a,"b":-1});
        }

    }, [counter,props.slideId.a,props.slideId.b,incrementCounter]);

    console.log("Hi There");

    return <div className={classes.ImageSlider} onChange={()=> props.radioButton(counter.a)}>
                {images[counter.a]}
           </div>

}

export default ImageSlider;
