import Toolbar from '../../components/Navigation/Toolbar/Toolbar';
import SideDrawer from '../../components/Navigation/SideDrawer/SideDrawer';

import { useDispatch, useSelector } from 'react-redux';

import {sideDrawerAction} from '../../store/actions/index';


const layout = (props) => {

    const dispatch = useDispatch();

    const showSideDrawerState = useSelector(state => {
        return state.layout.showSideDrawer;
    });

    const showSideDrawerMethod = () => {
        console.log("clicked su");
        dispatch(sideDrawerAction(showSideDrawerState));
    }


    return <div>
           <Toolbar showSideDrawer={showSideDrawerMethod}/>
           <SideDrawer/>
           <main>{props.children}</main>
    </div>

}


export default layout;
