import Toolbar from '../../components/Navigation/Toolbar/Toolbar';
import SideDrawer from '../../components/Navigation/SideDrawer/SideDrawer';
import Backdrop from '../../components/Navigation/SideDrawer/Backdrop/Backdrop';

import React, { useState } from 'react';


const Layout = (props) => {

    const [sideDrawer, setSideDrawer] = useState(false);

    const showSideDrawerMethod = () => {
        setSideDrawer(!sideDrawer);
    }

    return <div>
           <Backdrop sideDrawerState={sideDrawer}/>
           <Toolbar showSideDrawer={showSideDrawerMethod} sideDrawerState={sideDrawer}/>
           <SideDrawer sideDrawerState={sideDrawer}/>
           <main>{props.children}</main>
    </div>

}


export default Layout;
