import classes from './ImageSlider.module.css';
import RadioBtnSlider from './RadioBtnSlider/RadioBtnSlider';
import React, { useEffect,useState,useRef,useCallback } from 'react';
const ImageSlider = (props) => {

    const images = props.imageProperties;

    let [counter,setCounter] = useState(0);

    const controlSlider = (id) => {
        setCounter(id);
    }

    const incrementCounter = useCallback(() =>{
        if(counter === 7){
            setCounter(0);
        }else{
            setCounter(counter+1);
        };
    },[counter]);

    useEffect(() => {
            const timer = setTimeout(() => {
                incrementCounter();
            }, 10000);
            return () => clearTimeout(timer);

    }, [incrementCounter]);

    console.log("Hi There");

    const radioButtons = <RadioBtnSlider radioProperties={radioButtons} selectImage={controlSlider}/> 

    return <div>
               <div className={classes.ImageSlider}>
                   {images[counter.a]}
               </div>
               <div className={classes.RadioBtnSlider}>
                 {radioButtons}
                </div>
           </div>

}

export default ImageSlider;
