import sidedrawer from '../../components/Navigation/SideDrawer/SideDrawer';
import * as actionTypes from '../action/actionTypes';

export const  sideDrawer= (showSideDrawerState) => {
    return {
        type: actionTypes.SIDE_DRAWER_CONTROL,
        showSideDrawer:  showSideDrawerState
    };
};

export const sideDrawerAction = () => {
    return dispatch => {
        dispatch(sideDrawer(showSideDrawerState));
    };
};
