import classes from './ImageSlider.module.css';
import React, { useEffect,useState,useCallback } from 'react';
const ImageSlider = (props) => {

    let [counter,setCounter] = useState({"a":0,"b":0});

    const images = props.imageProperties.map(imageDetail => {
        return <div className={imageDetail.image_details}>
                    <img src={imageDetail.image} alt={imageDetail.category} key={imageDetail.image} />
               </div>
    });

    function updateMessage(){
        console.log("Yes");
        if(counter.a === 7){
            console.log("0: "+0);
            setCounter({"a":0,"b":0});
        }else{
            console.log({"a":counter.a+1,"b":counter.b});
            setCounter({"a":counter.a+1,"b":counter.b});
        };
    }

    useEffect(() => {

        if(counter.b === -1 || props.slideId === -1){
            const timer = setTimeout(() => {
                updateMessage();
            }, 10000);
            return () => clearTimeout(timer);
        }else{
            setCounter({"a":props.slideId,"b":-1});
        }

    }, [counter]);

    console.log("Hi There");


    return <div className={classes.ImageSlider}>
                {images[counter.a]}
           </div>

}

export default ImageSlider;
