import Toolbar from '../../components/Navigation/Toolbar/Toolbar';
import SideDrawer from '../../components/Navigation/SideDrawer/SideDrawer';

import { connect } from 'react-redux';

import {sideDrawerAction} from '../../store/actions/index';


const layout = (props) => {

    const showSideDrawerMethod = () => {
        console.log("clicked su");
        props.showSideDrawer(props.sideDrawerState);
    }


    return <div>
           <Toolbar showSideDrawer={showSideDrawerMethod}/>
           <SideDrawer/>
           <main>{props.children}</main>
    </div>

}

const mapStateToProps = state => {
    return {
      sideDrawerState: state.layout.showSideDrawer,
    };
  };

  const mapDispatchToProps = dispatch => {
    return {
      showSideDrawer: (sideDrawerState) =>dispatch(sideDrawerAction(sideDrawerState)),
    };
  };

export default connect(mapStateToProps,mapDispatchToProps)(layout);
