import classes from './Toolbar.module.css';
import NavigationItems from '../NavigationItems/NavigationItems';
import Logo from '../../Logo/Logo';
import DrawerToggle from '../SideDrawer/DrawerToggle/DrawerToggle';
import { Redirect } from 'react-router-dom';
import React, { useState } from 'react';


const Toolbar = (props) => {

   let drawerToggle = null;
   let authRedirect = null;

   if(!props.sideDrawerState){
      drawerToggle  = <DrawerToggle clicked={props.showSideDrawer} />
   }

   const [moveToHome, setMoveToHome] = useState(null);

   const navigateToHomePage = () => {
      setMoveToHome("toolbarCloseSideDrawer");
   }




   return <header className={classes.MainHeader}>
           {moveToHome=="toolbarCloseSideDrawer" || moveToHome=="closeSideDrawer"   ? <Redirect to="/" /> : null}
            <div className={classes.Toolbar}>
               {authRedirect}
               <div>
                   <Logo clicked={navigateToHomePage}/>
               </div>
               <div className={classes.MobileOnly}>
                  {drawerToggle}
               </div>
               <div className={classes.DesktopOnly}>
                   <NavigationItems closeSideDrawer={props.clicked}/> 
               </div>
            </div>
         </header>;

}

export default Toolbar;
