import classes from './ImageSlider.module.css';
import RadioBtnSlider from './RadioBtnSlider/RadioBtnSlider';
import React, { useEffect,useState,useCallback } from 'react';
const ImageSlider = (props) => {

    const images = props.imageProperties;

    const radioProperties = [{"id":0,"category":"Web App","checked": false},
                            {"id":1,"category":"Mobile App","checked": false},
                            {"id":2,"category":"Automation","checked": false},
                           ];

    let [counter,setCounter] = useState(0);



    const controlSlider = (id) => {
        setCounter(id);
    }

    const incrementCounter = useCallback(() =>{
        if(counter === 2){
            setCounter(0);
        }else{
            setCounter(counter+1);
        };
    },[counter]);

    useEffect(() => {
            const timer = setTimeout(() => {
                incrementCounter();
            }, 11000);
            return () => clearTimeout(timer);

    }, [incrementCounter]);

    console.log("Hi There");

    let updateRatioProperties = radioProperties.map(radioProperty =>{
                              return radioProperty.id === counter ? {...radioProperty,checked: true} : radioProperty;
                              });



    const radioButtons = <RadioBtnSlider radioProperties={updateRatioProperties} selectImage={controlSlider}/> 

    let  descriptionOfCategory= null;

    if(counter === 0){
        descriptionOfCategory =  <h1 className={classes.CategoryId_0}>{radioProperties[counter].category}</h1>
    }else if(counter === 1){
        descriptionOfCategory =  <h1 className={classes.CategoryId_1}>{radioProperties[counter].category}</h1>
    }else if(counter === 2){
        descriptionOfCategory =  <h1 className={classes.CategoryId_2}>{radioProperties[counter].category}</h1>
    }

    return <div className={classes.ImageSlider}>
                <div className={classes.DivOfImages}>
                    {images[counter]}
                </div>
                {descriptionOfCategory}
                {radioButtons}
           </div>

}

export default ImageSlider;
