      return state.layout.showSideDrawer;
   });
   console.log("11");
   console.log(showSideDrawerState);
   console.log("11");

   let sideDrawer = <div>
                        <div>
                           <Logo/>
                        </div>
                        <nav>
                           <NavigationItems /> 
                        </nav>
                     </div>;

   if(showSideDrawerState) {
       return sideDrawer;
   }else{
      return null;
   }
}

export default Sidedrawer;
