import classes from './Toolbar.module.css';
import NavigationItems from '../NavigationItems/NavigationItems';
import Logo from '../../Logo/Logo';
import DrawerToggle from '../SideDrawer/DrawerToggle/DrawerToggle';
import { Redirect } from 'react-router-dom';


import { useDispatch, useSelector } from 'react-redux';
import * as actions from '../../../store/actions/index';
import React, { useState,useEffect } from 'react';

const Toolbar = () => {

   const [moveToHome, setMoveToHome] = useState(false);

   const dispatch = useDispatch();
   const showDrawerState = useSelector(state => {
      return state.layout.showDrawerState;
   });

   const showSideDrawerMethod = () => dispatch(actions.sideDrawerAction(true));

   let drawerToggle = null;

   if(showDrawerState){
      drawerToggle  = <DrawerToggle clicked={showSideDrawerMethod} />
   }

   const navigateToHomePage = () => {
      setMoveToHome(true);
   }

   useEffect(()=>{
         setMoveToHome(false); 
   },[moveToHome]);

   console.log("renderToolbar");


   return <header className={classes.MainHeader}>
            {moveToHome ? <Redirect to="/" /> : null}
            <div className={classes.Toolbar}>
               <div>
                   <Logo clicked={navigateToHomePage}/>
               </div>
               <div className={classes.MobileOnly}>
                  {drawerToggle}
               </div>
               <div className={classes.DesktopOnly}>
                   <NavigationItems /> 
               </div>
            </div>
         </header>;

}

export default React.memo(Toolbar);
